mapper.select:
SELECT 

FROM sc_class a

WHERE a.id = #{id}

SELECT 

FROM sc_class a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.grade_id = #{gradeId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sc_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sc_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
