mapper.select:
SELECT 

FROM sc_resource a

WHERE a.id = #{id}

SELECT 

FROM sc_resource a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.resource_type = #{resourceType}


		AND a.total BETWEEN #{beginTotal} AND #{endTotal}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sc_resource a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sc_resource SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
