mapper.select:
SELECT 

FROM sc_student a

WHERE a.id = #{id}

SELECT 

FROM sc_student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.st_no = #{stNo}


		AND a.gender = #{gender}


		AND a.id_no = #{idNo}


		AND a.birthday = #{birthday}


		AND a.wechat = #{wechat}


		AND a.qq = #{qq}


		AND a.father_name = #{fatherName}


		AND a.mother_name = #{motherName}


		AND a.father_phone = #{fatherPhone}


		AND a.mother_phone = #{motherPhone}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sc_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sc_student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
