mapper.select:
SELECT 

FROM sc_student_event a

WHERE a.id = #{id}

SELECT 

FROM sc_student_event a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.event_time BETWEEN #{beginEventTime} AND #{endEventTime}


		AND a.event_type = #{eventType}


		AND a.content LIKE 
			'%'||#{content}||'%'
			'%'+#{content}+'%'
			concat('%',#{content},'%')


		AND a.student_id = #{studentId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sc_student_event a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sc_student_event SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
