mapper.select:
SELECT 

FROM sc_teacher a

WHERE a.id = #{id}

SELECT 

FROM sc_teacher a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.te_no = #{teNo}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.gender = #{gender}


		AND a.phone = #{phone}


		AND a.email = #{email}


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')


		AND a.id_no = #{idNo}


		AND a.course_id = #{courseId}


		AND a.level_id = #{levelId}


		AND a.job_id = #{jobId}


		AND a.birthday BETWEEN #{beginBirthday} AND #{endBirthday}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sc_teacher a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sc_teacher SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
